import React, { useState } from 'react'
import { Image } from 'react-bootstrap'
import Carousel from 'react-multi-carousel'
import 'react-multi-carousel/lib/styles.css'

import './Portfolio.css'
const Portfolio = () => {
  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  }
  const images = [
    require('../../images/image.png').default,
    require('../../images/image.png').default,
    require('../../images/image.png').default,
    require('../../images/image.png').default,
    require('../../images/examify3.png').default,
  ]
  return (
    <div id="works">
      <div className="text-center" id="works-text">
        <h2>
          Here are some of <span style={{ color: '#D93330' }}>our works</span>
        </h2>
        <p>
          We have been working with individuals as well as small and medium size
          companies to help them create wonderful digital products. Below is a
          selection of a few companies we have been working with, although not
          all of them are showcased here. If you would like to know more about
          other projects we have worked on, feel free to let us know so that we
          can share our brochure with you!
        </p>
      </div>

      <div id="works-carousel">
        <Carousel
          ssr
          // deviceType={deviceType}
          itemClass="image-item"
          responsive={responsive}
          infinite={true}
          showDots={true}
          autoPlay={false}
          autoPlaySpeed={3000}
        >
          {images.map((image) => {
            return (
              <Image
                fluid
                key={image}
                draggable={false}
                style={{
                  width: '440.1px',
                  height: '336.53px',
                }}
                src={image}
              />
            )
          })}
        </Carousel>
      </div>
    </div>
  )
}

export default Portfolio
